 1→package provider
 2→
 3→// Registry maps scheme names to providers.
 4→type Registry struct {
 5→	providers map[string]Provider
 6→}
 7→
 8→// NewRegistry creates an empty registry.
 9→func NewRegistry() *Registry {
10→	return &Registry{providers: make(map[string]Provider)}
11→}
12→
13→// Register adds a provider to the registry, keyed by its scheme.
14→func (r *Registry) Register(p Provider) {
15→	r.providers[p.Scheme()] = p
16→}
17→
18→// Lookup returns the provider for the given scheme.
19→func (r *Registry) Lookup(scheme string) (Provider, bool) {
20→	p, ok := r.providers[scheme]
21→	return p, ok
22→}
