 1→package provider
 2→
 3→import (
 4→	"fmt"
 5→	"strings"
 6→)
 7→
 8→// Target represents a parsed <scheme>:<identifier> input.
 9→type Target struct {
10→	Scheme     string
11→	Identifier string
12→}
13→
14→// ParseTarget parses a string in the form "scheme:identifier".
15→func ParseTarget(s string) (Target, error) {
16→	idx := strings.Index(s, ":")
17→	if idx < 0 {
18→		return Target{}, fmt.Errorf("invalid target %q: missing ':'", s)
19→	}
20→	scheme := s[:idx]
21→	id := s[idx+1:]
22→	if scheme == "" {
23→		return Target{}, fmt.Errorf("invalid target %q: empty scheme", s)
24→	}
25→	if id == "" {
26→		return Target{}, fmt.Errorf("invalid target %q: empty identifier", s)
27→	}
28→	return Target{Scheme: scheme, Identifier: id}, nil
29→}
