10→
11→// Provider wraps a provider.Provider with disk caching.
12→type Provider struct {
13→	underlying provider.Provider
14→	store      *Store
15→	noCache    bool
16→}
17→
18→// NewProvider creates a caching decorator around the given provider.
19→// When noCache is true, the cache is bypassed on reads but results are
20→// still written (so subsequent runs without --no-cache benefit).
21→func NewProvider(underlying provider.Provider, store *Store, noCache bool) *Provider {
22→	return &Provider{
23→		underlying: underlying,
24→		store:      store,
25→		noCache:    noCache,
26→	}
27→}
28→
29→func (p *Provider) Scheme() string {
30→	return p.underlying.Scheme()
31→}
32→
33→func (p *Provider) Fetch(ctx context.Context, identifier string) (provider.Result, error) {
34→	key := p.underlying.Scheme() + ":" + identifier
35→
36→	if !p.noCache {
37→		if cached, ok := p.store.Get(key); ok {
38→			return cached, nil
39→		}
40→	}
41→
42→	result, err := p.underlying.Fetch(ctx, identifier)
43→	if err != nil {
44→		return result, err
45→	}
46→
47→	if result.Error == "" {
48→		_ = p.store.Set(key, result)
49→	}
50→
51→	return result, nil
52→}
