 1→package cache
 2→
 3→import (
 4→	"crypto/sha256"
 5→	"encoding/hex"
 6→	"encoding/json"
 7→	"os"
 8→	"path/filepath"
 9→	"time"
10→
11→	"github.com/yutakobayashidev/repiq/internal/provider"
12→)
13→
14→// entry is the on-disk JSON structure for a cache entry.
15→type entry struct {
16→	CachedAt time.Time       `json:"cached_at"`
17→	Result   provider.Result `json:"result"`
18→}
19→
20→// Store is a file-based cache keyed by scheme:identifier.
21→type Store struct {
22→	dir string
23→	ttl time.Duration
24→}
25→
26→// NewStore creates a Store that writes JSON files to dir with the given TTL.
27→func NewStore(dir string, ttl time.Duration) *Store {
28→	return &Store{dir: dir, ttl: ttl}
29→}
30→
31→// path returns the file path for a given cache key using SHA-256 hash.
32→func (s *Store) path(key string) string {
33→	h := sha256.Sum256([]byte(key))
34→	return filepath.Join(s.dir, hex.EncodeToString(h[:])+".json")
35→}
36→
37→// Get retrieves a cached Result. Returns (Result, false) on miss or TTL expiry.
38→func (s *Store) Get(key string) (provider.Result, bool) {
39→	data, err := os.ReadFile(s.path(key))
40→	if err != nil {
41→		return provider.Result{}, false
42→	}
43→
44→	var e entry
45→	if err := json.Unmarshal(data, &e); err != nil {
46→		return provider.Result{}, false
47→	}
48→
49→	if time.Since(e.CachedAt) > s.ttl {
50→		return provider.Result{}, false
51→	}
52→
53→	return e.Result, true
54→}
55→
56→// Set writes a Result to the cache. The directory is created lazily.
57→// Writes are atomic: data is written to a temp file, then renamed.
58→func (s *Store) Set(key string, result provider.Result) error {
59→	if err := os.MkdirAll(s.dir, 0o755); err != nil {
60→		return err
61→	}
62→
63→	e := entry{
64→		CachedAt: time.Now(),
65→		Result:   result,
66→	}
67→	data, err := json.Marshal(e)
68→	if err != nil {
69→		return err
70→	}
71→
72→	tmp, err := os.CreateTemp(s.dir, "*.tmp")
73→	if err != nil {
74→		return err
75→	}
76→	tmpName := tmp.Name()
77→
78→	if _, err := tmp.Write(data); err != nil {
79→		_ = tmp.Close()
80→		_ = os.Remove(tmpName)
81→		return err
82→	}
83→	if err := tmp.Close(); err != nil {
84→		_ = os.Remove(tmpName)
85→		return err
86→	}
87→
88→	return os.Rename(tmpName, s.path(key))
89→}
