25→}
26→
27→// ResolveToken returns a token using the priority: gh auth token > GITHUB_TOKEN > empty.
28→func (r *Resolver) ResolveToken() string {
29→	if out, err := r.Cmd.Run("gh", "auth", "token"); err == nil {
30→		if tok := strings.TrimSpace(out); tok != "" {
31→			return tok
32→		}
33→	}
34→	if tok := r.Getenv("GITHUB_TOKEN"); tok != "" {
35→		return tok
36→	}
37→	return ""
38→}
