 1→package auth
 2→
 3→import (
 4→	"os/exec"
 5→	"strings"
 6→)
 7→
 8→// CmdRunner abstracts external command execution for testability.
 9→type CmdRunner interface {
10→	Run(name string, args ...string) (string, error)
11→}
12→
13→// ExecRunner executes real OS commands.
14→type ExecRunner struct{}
15→
16→func (ExecRunner) Run(name string, args ...string) (string, error) {
17→	out, err := exec.Command(name, args...).Output()
18→	return string(out), err
19→}
20→
21→// Resolver resolves a GitHub authentication token.
22→type Resolver struct {
23→	Cmd    CmdRunner
24→	Getenv func(string) string
25→}
26→
27→// ResolveToken returns a token using the priority: gh auth token > GITHUB_TOKEN > empty.
28→func (r *Resolver) ResolveToken() string {
29→	if out, err := r.Cmd.Run("gh", "auth", "token"); err == nil {
30→		if tok := strings.TrimSpace(out); tok != "" {
31→			return tok
32→		}
33→	}
34→	if tok := r.Getenv("GITHUB_TOKEN"); tok != "" {
35→		return tok
36→	}
37→	return ""
38→}
