 1→{
 2→  description = "A Nix-flake-based Go development environment";
 3→
 4→  inputs = {
 5→    nixpkgs.url = "https://flakehub.com/f/NixOS/nixpkgs/0.1"; # unstable Nixpkgs
 6→    git-hooks = {
 7→      url = "github:cachix/git-hooks.nix";
 8→      inputs.nixpkgs.follows = "nixpkgs";
 9→    };
10→  };
11→
12→  outputs =
13→    { self, ... }@inputs:
14→
15→    let
16→      goVersion = 24; # Change this to update the whole stack
17→
18→      supportedSystems = [
19→        "x86_64-linux"
20→        "aarch64-linux"
21→        "x86_64-darwin"
22→        "aarch64-darwin"
23→      ];
24→      forEachSupportedSystem =
25→        f:
26→        inputs.nixpkgs.lib.genAttrs supportedSystems (
27→          system:
28→          f {
29→            pkgs = import inputs.nixpkgs {
30→              inherit system;
31→              overlays = [ inputs.self.overlays.default ];
32→            };
33→            inherit system;
34→          }
35→        );
36→    in
37→    {
38→      overlays.default = final: prev: {
39→        go = final."go_1_${toString goVersion}";
40→      };
41→
42→      checks = forEachSupportedSystem (
43→        { pkgs, system }:
44→        {
45→          git-hooks = inputs.git-hooks.lib.${system}.run {
46→            src = ./.;
47→            hooks = {
48→              commitizen.enable = true;
49→              gitleaks = {
50→                enable = true;
51→                name = "gitleaks";
52→                entry = "${pkgs.gitleaks}/bin/gitleaks detect --source . --verbose --redact";
53→                language = "system";
54→                pass_filenames = false;
55→              };
56→            };
57→          };
58→        }
59→      );
60→
61→      devShells = forEachSupportedSystem (
62→        { pkgs, system }:
63→        {
64→          default = pkgs.mkShellNoCC {
65→            inherit (self.checks.${system}.git-hooks) shellHook;
66→            packages = with pkgs; [
67→              # go (version is specified by overlay)
68→              go
69→
70→              # goimports, godoc, etc.
71→              gotools
72→
73→              # https://github.com/golangci/golangci-lint
74→              golangci-lint
75→            ];
76→          };
77→        }
78→      );
79→    };
80→}
81→

